// Sancionados por Año
val sanctionedPerYear = sanctioned.groupBy($"year").count().orderBy($"year")

printZeppelinTable(sanctionedPerYear, List("Año", "Cantidad de Proyecto Sancionados por Año"), $"year", $"count")

// Medias Sanciones por Año
val halfSanctionedPerYear = halfSanctioned.groupBy($"year").count().orderBy($"year")

printZeppelinTable(halfSanctionedPerYear, List("Año", "Cantidad de Proyectos con Media Sancion por Año"), $"year", $"count")

// Retirados por Año
val retiredPerYear = retired.groupBy($"year").count().orderBy($"year")

printZeppelinTable(retiredPerYear, List("Año", "Cantidad de Proyectos Retirados por Año"), $"year", $"count")
