// Sancionados por Año
val sanctionedPerYear = sanctioned.groupBy($"year").count().orderBy($"year")

printZeppelinTable(sanctionedPerYear, List("Año", "Cantidad de Proyecto Sancionados por Año"), $"year", $"count")

// Medias Sanciones por Año
val halfSanctionedPerYear = halfSanctioned.groupBy($"year").count().orderBy($"year")

printZeppelinTable(halfSanctionedPerYear, List("Año", "Cantidad de Proyectos con Media Sancion por Año"), $"year", $"count")

// Retirados por Año
val retiredPerYear = retired.groupBy($"year").count().orderBy($"year")

printZeppelinTable(retiredPerYear, List("Año", "Cantidad de Proyectos Retirados por Año"), $"year", $"count")

// Proyectos totales por partido.
val signersByParty = signers.groupBy($"party").agg(countDistinct($"id").as("count")).orderBy(desc("count"))

printZeppelinTable(signersByParty, List("Partido", "Cantidad de Proyectos Presentados"), $"party", $"count")

//Top 20 Cantidad de proyectos presentados por diputado
val topSigners = signers.groupBy($"congressman").agg(count($"id").as("amount")).orderBy(desc("amount")).limit(20)
printZeppelinTable(topSigners, List("Diputado", "Cantidad de Proyectos Presentados"), $"congressman", $"amount")

//Bottom 20 Cantidad de proyectos presentados por diputado
val bottomSigners = signers.groupBy("congressman").agg(count("id").as("amount")).sort(asc("amount")).limit(20)
printZeppelinTable(bottomSigners, List("Diputado", "Cantidad de Proyectos Presentados"), $"congressman", $"amount")

//Cantidad de partidos firmantes por proyecto
val partiesPerProject = signers.groupBy("id").agg(countDistinct("party").as("partiesperproject")).groupBy("partiesperproject").count()

//Top 20
val PPP = partiesPerProject.sort(asc("partiesperproject"))
printZeppelinTable(PPP, List("Partidos Distintos", "Cantidad de Proyectos Presentados"), $"partiesperproject", $"count")
